import React from 'react';
import './App.css';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import AsyncSelect from 'react-select/async';
import Select from 'react-select';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

const priorities = [
	{ value: 'Low', label: 'Low' },
	{ value: 'Medium', label: 'Medium' },
	{ value: 'High', label: 'High' },
	{ value: 'Time-sensitive', label: 'Time-sensitive' },
	{ value: 'Daily routine', label: 'Daily routine' },
	{ value: 'Urgent', label: 'Urgent' },
];

const Airtable = require('airtable');
const base = new Airtable({apiKey: 'keymrBNLy7tXIfV2H'}).base('apptbLLUUnap2S6oD');
var tagList = [];

const dbGetTags = (theTagList) => {
	tagList = theTagList;
};

const getOptions = (inputValue: string) => {
	return tagList.filter(i =>
		i.label.toLowerCase().includes(inputValue.toLowerCase())
	);
};

const loadOptions = (inputValue, callback) => {
	GetTags(base, dbGetTags);
	setTimeout(() => {
		callback(getOptions(inputValue));
	}, 3000);
};

export function GetTags(base, callbackFn) {
	var recordList = [];
	base('Tags').select({
	}).eachPage(function page(records, fetchNextPage) {
		records.forEach(function(record) {
			let newRecord = { value: record.id, label: record.get('Name') };
			recordList.push(newRecord);
		});
		fetchNextPage();
	}, function done(err) {
		if (err) { console.error(err); return; } else {
			callbackFn(recordList);
		}
	});
};

export function GetDateString(theDate) {
	var dt = new Date(theDate);
	let year = dt.getFullYear().toString();
	let month = dt.getMonth() + 1;
	month = month < 10 ? '0' + month : month;
	let dayOfMonth = dt.getDate() < 10 ? '0' + dt.getDate() : dt.getDate();
	let str = ""+year+"-"+month+"-"+dayOfMonth;
	return str;
}

class App extends React.Component {

	constructor (props) {
		super(props)

		this.state = {
		  startDate: new Date(),
		  endDate: new Date(),
		  doRepeat: false,
		  doDaily: false,
		  showDB: false,
		  showLOG: false,
		  repeatType: 'Daily',
		  repinterval: 1,
		  repIntervalType: '',
		  selectedItems: [],
		  taskName: '',
		  priority: '',
		  nHrs: 0.0,
		  notes: '',
		  dailyType: '',
		  logStr: "",
		};
		this.handleStartChange = this.handleStartChange.bind(this);
		this.handleEndChange = this.handleEndChange.bind(this);
		this.handleRepeatChange = this.handleRepeatChange.bind(this);
		this.handleDoDailyChange = this.handleDoDailyChange.bind(this);
		this.handleShowDBChange = this.handleShowDBChange.bind(this);
		this.handleShowLOGChange = this.handleShowLOGChange.bind(this);
		this.handleRepIntervalChange = this.handleRepIntervalChange.bind(this);
		this.handleRepStepChange = this.handleRepStepChange.bind(this);
		this.handleRepIntTypeChange = this.handleRepIntTypeChange.bind(this);
		this.handleSelectTags = this.handleSelectTags.bind(this);
		this.handleTaskChange = this.handleTaskChange.bind(this);
		this.handleSelectPriority = this.handleSelectPriority.bind(this);
		this.handleHrsChange = this.handleHrsChange.bind(this);
		this.handleNotesChange = this.handleNotesChange.bind(this);
		this.handleDailyTypeChange = this.handleDailyTypeChange.bind(this);
		this.handleFillDaily = this.handleFillDaily.bind(this);
		this.onFormSubmit = this.onFormSubmit.bind(this);
	  }


	handleStartChange(date) {
		this.setState({startDate: date});
	}

	handleEndChange(date) {
		this.setState({endDate: date});
	}

	handleRepeatChange(event) {
		this.setState({doRepeat: event.target.checked});
	}

	handleDoDailyChange(event) {
		this.setState({doDaily: event.target.checked});
	}

	handleShowDBChange(event) {
		this.setState({showDB: event.target.checked});
	}

	handleShowLOGChange(event) {
		this.setState({showLOG: event.target.checked});
	}

	handleTaskChange(event) {
		this.setState({taskName: event.target.value});
	}

	handleNotesChange(event) {
		this.setState({notes: event.target.value});
	}

	onFormSubmit(e) {
		e.preventDefault();
		if (this.state.doDaily) {
			alert('DAILY!');
		} else {
			this.addDBRecord();
		}
	}

	handleSelectTags(selectedItems) {
		this.setState({selectedItems: selectedItems});
	}

	handleSelectPriority(priority) {
		this.setState({priority: priority});
	}

	handleHrsChange(event) {
		this.setState({nHrs: event.target.value});
	}

	handleRepIntervalChange(event) {
		this.setState({repeatType: event.target.value});
	}

	handleRepStepChange(event) {
		this.setState({repinterval: event.target.value});
	}

	handleRepIntTypeChange(event) {
		this.setState({repIntervalType: event.target.value});
	}

	handleDailyTypeChange(event) {
		this.setState({dailyType: event.target.value});
	}

	handleFillDaily(event) {
		this.setState({
			taskName: this.state.dailyType,
			priority: { value: 'Daily routine', label: 'Daily routine' },
			notes: '',
		});
		var tagDailyRoutine = tagList.find(function (element) {
			return element.label === "Daily routine";
		});
		var tagPersonal = tagList.find(function (element) {
			return element.label === "Personal";
		});
		var tagFamily = tagList.find(function (element) {
			return element.label === "Family";
		});
		var tagExercise = tagList.find(function (element) {
			return element.label === "Exercise";
		});
		switch(this.state.dailyType) {
			case 'Wind down':
				this.setState({
					nHrs: 1.0,
					selectedItems: [tagDailyRoutine, tagPersonal],
				});
				break;
			case 'Sleep':
				this.setState({
					nHrs: 7.0,
					selectedItems: [tagDailyRoutine, tagPersonal],
				});
				break;
			case 'Morning':
				this.setState({
					nHrs: 2.0,
					selectedItems: [tagDailyRoutine, tagPersonal],
				});
				break;
			case 'Lunch':
				this.setState({
					nHrs: 0.5,
					selectedItems: [tagDailyRoutine, tagPersonal],
				});
				break;
			case 'Walk':
				this.setState({
					nHrs: 0.5,
					selectedItems: [tagDailyRoutine, tagFamily, tagExercise],
				});
				break;
			case 'Dinner':
				this.setState({
					nHrs: 1.5,
					selectedItems: [tagDailyRoutine, tagFamily],
				});
				break;
			case 'Evening family':
				this.setState({
					nHrs: 1.0,
					selectedItems: [tagDailyRoutine, tagFamily],
				});
				break;
			default:
		};
	}

	addDBRecord() {
		console.log("SUBMIT:");
		console.log("  Task name = ", this.state.taskName);
		console.log("  Tags = ", this.state.selectedItems);
		console.log("  Priority = ", this.state.priority);
		console.log("  Number of hours = ", this.state.nHrs);
		console.log("  Date = ", this.state.startDate.getFullYear(), "/", this.state.startDate.getMonth()+1, "/", this.state.startDate.getDate());
		console.log("  Notes = ", this.state.notes);
		var theLog = "SUBMIT:\n";
		theLog = theLog + "  Task name = " + this.state.taskName + "\n";
		theLog = theLog + "  Tags = ";
		var selectedTags = [];
		this.state.selectedItems.forEach(item => {
			theLog = theLog + item.label + ", ";
			selectedTags.push(item.value);
		});
		theLog = theLog + "\n";
		theLog = theLog + "  Priority = " + this.state.priority.label + "\n";
		theLog = theLog + "  Number of hours = " + this.state.nHrs + "\n";
		theLog = theLog + "  Notes = " + this.state.notes + "\n";

		let newRecord = {
			"fields": {
				"Task": this.state.taskName,
				"Tag": selectedTags,
				"Hrs": parseFloat(this.state.nHrs),
				"Priority": this.state.priority.value,
				"Due date": GetDateString(this.state.startDate),
				"Notes": this.state.notes,
			}
		};
		if (this.state.doRepeat) {
			theLog = theLog + "  Start date = " + this.state.startDate.getFullYear() + "/" + (this.state.startDate.getMonth()+1) + "/" + this.state.startDate.getDate() + "\n";
