							], function(err, records) {
							if (err) {
							  console.error(err);
							  return;
							}
						});
					}
					console.log("  MONTHLY");
					break;
				case "Every":
					switch(this.state.repIntervalType) {
						case "Days":
							theLog = theLog + "  Repeat every " + this.state.repinterval.toString() + " day:\n";
							for (; d <= this.state.endDate; d.setDate(parseInt(d.getDate())+parseInt(this.state.repinterval))) {
								newRecord.fields['Due date'] = GetDateString(d);
								theLog = theLog + "    " + newRecord.fields.Task + " | " + newRecord.fields.Tag + " | " + newRecord.fields.Hrs + " | " + newRecord.fields.Priority + " | " + newRecord.fields['Due date'] + " | " + newRecord.fields.Notes + "\n";
								base('Task List').create([
									newRecord,
									], function(err, records) {
									if (err) {
									  console.error(err);
									  return;
									}
								});
							}
							console.log("  DAILY");
							break;
						case "Weeks":
							theLog = theLog + "  Repeat every " + this.state.repinterval.toString() + " week:\n";
							for (; d <= this.state.endDate; d.setDate(parseInt(d.getDate())+7*parseInt(this.state.repinterval))) {
								newRecord.fields['Due date'] = GetDateString(d);
								theLog = theLog + "    " + newRecord.fields.Task + " | " + newRecord.fields.Tag + " | " + newRecord.fields.Hrs + " | " + newRecord.fields.Priority + " | " + newRecord.fields['Due date'] + " | " + newRecord.fields.Notes + "\n";
								base('Task List').create([
									newRecord,
									], function(err, records) {
									if (err) {
									  console.error(err);
									  return;
									}
								});
							}
							console.log("  WEEKLY");
							break;
						case "Months":
							theLog = theLog + "  Repeat every " + this.state.repinterval.toString() + " month:\n";
							for (; d <= this.state.endDate; d.setMonth(parseInt(d.getMonth())+parseInt(this.state.repinterval))) {
								newRecord.fields['Due date'] = GetDateString(d);
								theLog = theLog + "    " + newRecord.fields.Task + " | " + newRecord.fields.Tag + " | " + newRecord.fields.Hrs + " | " + newRecord.fields.Priority + " | " + newRecord.fields['Due date'] + " | " + newRecord.fields.Notes + "\n";
								base('Task List').create([
									newRecord,
									], function(err, records) {
									if (err) {
									  console.error(err);
									  return;
									}
								});
							}
							console.log("  MONTHLY");
							break;
						default:
							theLog = theLog + "  *** Bad repeat interval type";
							console.log("  *** Bad repeat interval type");
					}
					console.log("  EVERY");
					break;
				default:
					theLog = theLog + "  *** Bad repeat type";
					console.log("  *** Bad repeat type");
			}
		} else {
			theLog = theLog + "  Date = " + this.state.startDate.getFullYear() + "/" + (this.state.startDate.getMonth()+1) + "/" + this.state.startDate.getDate() + "\n";
