import React from 'react';
import './App.css';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import AsyncSelect from 'react-select/async';
import Select from 'react-select';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import { priorities, base, tagList, loadOptions } from './database';
import { EventHandler, SubmitForm } from './eventhandlers';

export function GetDateString(theDate) {
	var dt = new Date(theDate);
	let year = dt.getFullYear().toString();
	let month = dt.getMonth() + 1;
	month = month < 10 ? '0' + month : month;
	let dayOfMonth = dt.getDate() < 10 ? '0' + dt.getDate() : dt.getDate();
	let str = ""+year+"-"+month+"-"+dayOfMonth;
	return str;
}

class App extends React.Component {

	constructor (props) {
		super(props)

		this.state = {
		  startDate: new Date(),
		  endDate: new Date(),
		  doRepeat: false,
		  doDaily: false,
		  showDB: false,
		  showLOG: false,
		  repeatType: 'Daily',
		  repinterval: 1,
		  repIntervalType: '',
		  selectedItems: [],
		  taskName: '',
		  priority: '',
		  nHrs: 0.0,
		  notes: '',
		  logStr: "",
		};
		this.handleEvent = this.handleEvent.bind(this);
		this.handleStartChange = this.handleStartChange.bind(this);
		this.handleEndChange = this.handleEndChange.bind(this);
		this.handleSelectTags = this.handleSelectTags.bind(this);
		this.handleSelectPriority = this.handleSelectPriority.bind(this);
		this.onFormSubmit = this.onFormSubmit.bind(this);
	  }


	handleStartChange(date) {
		this.setState({startDate: date});
	}

	handleEndChange(date) {
		this.setState({endDate: date});
	}

	handleEvent(event) {
		EventHandler(this, event);
	}

	onFormSubmit(e) {
		e.preventDefault();
		setTimeout(() => {
			SubmitForm(this, tagList);
		}, 3000);
	}

	handleSelectTags(selectedItems) {
		this.setState({selectedItems: selectedItems});
	}

	handleSelectPriority(priority) {
		this.setState({priority: priority});
	}

	addDBRecord() {
		console.log("SUBMIT:");
		console.log("  Task name = ", this.state.taskName);
		console.log("  Tags = ", this.state.selectedItems);
		console.log("  Priority = ", this.state.priority);
		console.log("  Number of hours = ", this.state.nHrs);
		console.log("  Date = ", this.state.startDate.getFullYear(), "/", this.state.startDate.getMonth()+1, "/", this.state.startDate.getDate());
		console.log("  Notes = ", this.state.notes);
		var theLog = "SUBMIT:\n";
		theLog = theLog + "  Task name = " + this.state.taskName + "\n";
		theLog = theLog + "  Tags = ";
		var selectedTags = [];
		this.state.selectedItems.forEach(item => {
			theLog = theLog + item.label + ", ";
			selectedTags.push(item.value);
		});
		theLog = theLog + "\n";
		theLog = theLog + "  Priority = " + this.state.priority.label + "\n";
		theLog = theLog + "  Number of hours = " + this.state.nHrs + "\n";
		theLog = theLog + "  Notes = " + this.state.notes + "\n";

		let newRecord = {
			"fields": {
				"Task": this.state.taskName,
				"Tag": selectedTags,
				"Hrs": parseFloat(this.state.nHrs),
				"Priority": this.state.priority.value,
				"Due date": GetDateString(this.state.startDate),
				"Notes": this.state.notes,
			}
		};
		if (this.state.doRepeat) {
			theLog = theLog + "  Start date = " + this.state.startDate.getFullYear() + "/" + (this.state.startDate.getMonth()+1) + "/" + this.state.startDate.getDate() + "\n";
			theLog = theLog + "  End date = " + this.state.endDate.getFullYear() + "/" + (this.state.endDate.getMonth()+1) + "/" + this.state.endDate.getDate() + "\n";
			var d = new Date(this.state.startDate); 
			switch(this.state.repeatType) {
				case "Daily":
					theLog = theLog + "  Repeat daily:\n";
					for (; d <= this.state.endDate; d.setDate(d.getDate()+1)) {
						newRecord.fields['Due date'] = GetDateString(d);
						theLog = theLog + "    " + newRecord.fields.Task + " | " + newRecord.fields.Tag + " | " + newRecord.fields.Hrs + " | " + newRecord.fields.Priority + " | " + newRecord.fields['Due date'] + " | " + newRecord.fields.Notes + "\n";
						base('Task List').create([
							newRecord,
							], function(err, records) {
							if (err) {
							  console.error(err);
							  return;
							}
						});
					}
					console.log("  DAILY");
					break;
				case "Weekly":
					theLog = theLog + "  Repeat weekly:\n";
					for (; d <= this.state.endDate; d.setDate(d.getDate()+7)) {
						newRecord.fields['Due date'] = GetDateString(d);
						theLog = theLog + "    " + newRecord.fields.Task + " | " + newRecord.fields.Tag + " | " + newRecord.fields.Hrs + " | " + newRecord.fields.Priority + " | " + newRecord.fields['Due date'] + " | " + newRecord.fields.Notes + "\n";
						base('Task List').create([
							newRecord,
							], function(err, records) {
							if (err) {
							  console.error(err);
							  return;
							}
						});
					}
					console.log("  WEEKLY");
					break;
				case "Monthly":
					theLog = theLog + "  Repeat monthly:\n";
					for (; d <= this.state.endDate; d.setMonth(d.getMonth()+1)) {
						newRecord.fields['Due date'] = GetDateString(d);
						theLog = theLog + "    " + newRecord.fields.Task + " | " + newRecord.fields.Tag + " | " + newRecord.fields.Hrs + " | " + newRecord.fields.Priority + " | " + newRecord.fields['Due date'] + " | " + newRecord.fields.Notes + "\n";
						base('Task List').create([
							newRecord,
							], function(err, records) {
							if (err) {
							  console.error(err);
							  return;
							}
						});
					}
					console.log("  MONTHLY");
					break;
				case "Every":
					switch(this.state.repIntervalType) {
						case "Days":
							theLog = theLog + "  Repeat every " + this.state.repinterval.toString() + " day:\n";
							for (; d <= this.state.endDate; d.setDate(parseInt(d.getDate())+parseInt(this.state.repinterval))) {
								newRecord.fields['Due date'] = GetDateString(d);
								theLog = theLog + "    " + newRecord.fields.Task + " | " + newRecord.fields.Tag + " | " + newRecord.fields.Hrs + " | " + newRecord.fields.Priority + " | " + newRecord.fields['Due date'] + " | " + newRecord.fields.Notes + "\n";
								base('Task List').create([
									newRecord,
									], function(err, records) {
									if (err) {
									  console.error(err);
									  return;
									}
								});
							}
							console.log("  DAILY");
							break;
						case "Weeks":
							theLog = theLog + "  Repeat every " + this.state.repinterval.toString() + " week:\n";
							for (; d <= this.state.endDate; d.setDate(parseInt(d.getDate())+7*parseInt(this.state.repinterval))) {
								newRecord.fields['Due date'] = GetDateString(d);
								theLog = theLog + "    " + newRecord.fields.Task + " | " + newRecord.fields.Tag + " | " + newRecord.fields.Hrs + " | " + newRecord.fields.Priority + " | " + newRecord.fields['Due date'] + " | " + newRecord.fields.Notes + "\n";
								base('Task List').create([
									newRecord,
									], function(err, records) {
									if (err) {
									  console.error(err);
									  return;
									}
								});
							}
							console.log("  WEEKLY");
							break;
						case "Months":
							theLog = theLog + "  Repeat every " + this.state.repinterval.toString() + " month:\n";
							for (; d <= this.state.endDate; d.setMonth(parseInt(d.getMonth())+parseInt(this.state.repinterval))) {
								newRecord.fields['Due date'] = GetDateString(d);
								theLog = theLog + "    " + newRecord.fields.Task + " | " + newRecord.fields.Tag + " | " + newRecord.fields.Hrs + " | " + newRecord.fields.Priority + " | " + newRecord.fields['Due date'] + " | " + newRecord.fields.Notes + "\n";
								base('Task List').create([
									newRecord,
									], function(err, records) {
									if (err) {
									  console.error(err);
									  return;
									}
								});
							}
							console.log("  MONTHLY");
							break;
						default:
							theLog = theLog + "  *** Bad repeat interval type";
							console.log("  *** Bad repeat interval type");
					}
					console.log("  EVERY");
					break;
				default:
					theLog = theLog + "  *** Bad repeat type";
					console.log("  *** Bad repeat type");
			}
		} else {
			theLog = theLog + "  Date = " + this.state.startDate.getFullYear() + "/" + (this.state.startDate.getMonth()+1) + "/" + this.state.startDate.getDate() + "\n";
			theLog = theLog + "\n\n\n" + newRecord.fields.Task + " | " + newRecord.fields.Tag + " | " + newRecord.fields.Hrs + " | " + newRecord.fields.Priority + " | " + newRecord.fields['Due date'] + " | " + newRecord.fields.Notes + "\n";
			base('Task List').create([
				newRecord,
				], function(err, records) {
				if (err) {
				  console.error(err);
				  return;
				}
			});
		}
		this.setState({ logStr: theLog });
	}

	render() {
		return (
			<div>
				<form onSubmit={ this.onFormSubmit } style={{display: 'flex',  justifyContent:'center', alignItems:'center', width: '100vh', height: '150vh'}}>
				<div className="form-group" >
					<div><table><tbody>
						<tr><td>
							<label>
								<div><b>Enter new task:</b></div>
							</label>
						</td></tr>
						<tr><td>
							<p>&nbsp;</p>
							<label>
								Daily&nbsp;&nbsp;
								<input name="doDaily" type="checkbox" checked={this.state.doDaily} onChange={this.handleEvent}></input>
							</label>
						</td></tr>
						<tr>
						{this.state.doDaily ?
							null
								:
							<td>
								<TextField required id="taskname" name="taskname" label="Task:" value={this.state.taskName} onChange={this.handleEvent} />
							</td>
						}
						</tr>
						{this.state.doDaily ?
							null
								:
							<div>
								<tr><td>
									<p>&nbsp;</p>
									<div>
										<div>Tags:</div>
										<AsyncSelect
											isMulti
											loadOptions={loadOptions}
											defaultOptions
											isSearchable={true}
											closeMenuOnSelect={false}
											onChange={this.handleSelectTags}
										>
										</AsyncSelect>
									</div>
								</td></tr>
								<tr><td>
									<p>&nbsp;</p>
									<div>
										<div>Priority:</div>
										<Select
											options={priorities}
											isSearchable={true}
											closeMenuOnSelect={true}
											onChange={this.handleSelectPriority}
										>
										</Select>
									</div>
								</td></tr>
								<tr><td>
									<p>&nbsp;</p>
									<TextField required id="hrs" name="hrs" label="Hrs:" type="number" inputProps={{step: "any", min: 0}} value={this.state.nHrs} onChange={this.handleEvent} />
								</td></tr>
							</div>
						}
						<tr><td>
							<p>&nbsp;</p>
							<div>Date:</div>
							<DatePicker
								selected={ this.state.startDate }
								onChange={ this.handleStartChange }
								name="startDate"
								dateFormat="MM/dd/yyyy"
							/>
						</td></tr>
						<tr><td>
							<p>&nbsp;</p>
							<label>
								Repeat?&nbsp;&nbsp;
								<input name="doRepeat" type="checkbox" checked={this.state.doRepeat} onChange={this.handleEvent}></input>
							</label>
						</td></tr>
						{this.state.doRepeat ?
							<div>
							<FormControl component="fieldset">
								<RadioGroup aria-label="repeattype" name="repeattype1" value={this.state.repeatType} onChange={this.handleEvent}>
									<tr><td><FormControlLabel value="Daily" control={<Radio />} label="Daily" /></td></tr>
									<tr><td><FormControlLabel value="Weekly" control={<Radio />} label="Weekly" /></td></tr>
									<tr><td><FormControlLabel value="Monthly" control={<Radio />} label="Monthly" /></td></tr>
									<tr>
										<td><FormControlLabel value="Every" control={<Radio />} label="Every" /></td>
										<td><input type="number" name="repintervalamt" value={this.state.repinterval} min="1" onChange={this.handleEvent}></input></td>
										<td>
											<FormControl component="fieldset">
												<RadioGroup aria-label="repeatinterval" name="repeatinterval1" value={this.state.repIntervalType} onChange={this.handleEvent}>
													<FormControlLabel value="Days" control={<Radio />} label="Days" />
													<FormControlLabel value="Weeks" control={<Radio />} label="Weeks" />
													<FormControlLabel value="Months" control={<Radio />} label="Months" />
												</RadioGroup>
											</FormControl>
										</td>
									</tr>
								</RadioGroup>
							</FormControl>
							<tr><td>
								<div>End Date:</div>
								<DatePicker
									selected={ this.state.endDate }
									onChange={ this.handleEndChange }
									name="endDate"
									dateFormat="MM/dd/yyyy"
							/>
							</td></tr>
							</div>
								: null}
						{this.state.doDaily ?
							null
							:
							<tr><td>
								<p>&nbsp;</p>
								<label>
									<div>Notes:</div>
									<textarea name="notes" value={this.state.notes} onChange={this.handleEvent} />
								</label>
							</td></tr>
						}
						<tr><td>
							<p>&nbsp;</p>
							<div><input type="submit" value="Submit" /></div>
						</td></tr>
						<tr>
							<td>
								<p>&nbsp;</p>
								<label>
									Show DB?&nbsp;&nbsp;
									<input name="showDB" type="checkbox" checked={this.state.showDB} onChange={this.handleEvent}></input>
								</label>
							</td>
							<td>
								<p>&nbsp;</p>
								<label>
									Show Log?&nbsp;&nbsp;
									<input name="showLog" type="checkbox" checked={this.state.showLOG} onChange={this.handleEvent}></input>
								</label>
							</td>
						</tr>
					</tbody></table></div>
				</div>
				</form>
				{this.state.showDB ?
					<div>
					<iframe
						className="airtable-embed"
						title="Tasks"
						src="https://airtable.com/embed/shrwDMaLicVlxs8oD?backgroundColor=gray"
						frameBorder="0"
						width="100%"
						height="533"
					>
					</iframe>
					</div>
					: null}
				{this.state.showLOG ?
					<div>
						<p>&nbsp;</p>
						<i>LOG:</i><div align="center"><textarea readOnly value={this.state.logStr} rows="10" cols="50" /></div>
					</div>
					: null}
			</div>
		);
	}
}

export default App;
